def get_nutrition_info(food_class):
    """Get nutrition information for a given Food-101 class"""
    if not food_class:
        return {
            'calories': 200,
            'protein': 8,
            'carbs': 25,
            'fat': 8,
            'fiber': 2,
            'sugar': 5,
            'sodium': 300,
            'serving_size': '200g'
        }
    key = food_class.lower().strip()
    return NUTRITION_DB.get(key, {
        'calories': 250,
        'protein': 10,
        'carbs': 30,
        'fat': 12,
        'fiber': 2,
        'sugar': 5,
        'sodium': 400,
        'serving_size': '200g'
    })
